printAoC2015Day01Answer :: IO ()
printAoC2015Day01Answer = do
  putStrLn "------ Day 01 ------"
  putStrLn "Recursive"
  putStrLn $ "part1: " ++ show part1
  putStrLn $ "part2: " ++ show part2
  putStrLn ""
  putStrLn "scanl"
  putStrLn $ "part1: " ++ show part1_1
  putStrLn $ "part2: " ++ show part2_1
  putStrLn ""
  putStrLn "foldl"
  putStrLn $ "part1: " ++ show part1_2
  putStrLn $ "part2: " ++ show part2_2
  putStrLn ""

calcNextFloor :: Char -> Int
calcNextFloor c
  | c == '(' = 1
  | c == ')' = -1
  | otherwise = 0

floorDirections :: List Int
floorDirections = calcNextFloor <$> input

data Acc = Acc Int Int deriving (Show)

startAcc :: Acc
startAcc = Acc 0 0

-- Recursive soltions
part1 :: Int
part1 = sum floorDirections

part2 :: Int
part2 = run startAcc floorDirections
 where
  run _ [] = -1
  run (Acc (-1) i) _ = i
  run (Acc f i) (x : xs) = run (Acc (f + x) (i + 1)) xs

-- Alternate solutions using scanl and takewhile
part1_1 :: Int
part1_1 = last $ scanl1 (+) floorDirections

part2_1 :: Int
part2_1 = (+ 1) $ length $ scanlUntil floorDirections
 where
  scanlUntil = takeWhile (/= (-1)) . scanl1 (+)

-- Alternate solutions using fold
part1_2 :: Int
part1_2 = foldl1 (+) floorDirections

foldlWhile :: (b -> a -> b) -> (b -> Bool) -> b -> List a -> b
foldlWhile _ _ acc [] = acc
foldlWhile f pred acc (x : xs) =
  if pred acc
    then acc
    else foldlWhile f pred (f acc x) xs

part2_2 :: Int
part2_2 = i
 where
  countFn acc _ = acc + 1
  foldFn acc x = acc + x
  accFn (Acc b c) a = Acc (countFn b a) (foldFn c a)
  predFn = (\(Acc _ b) -> b == (-1))
  (Acc i _) = foldlWhile accFn predFn startAcc floorDirections
