printAoC2015Day01Answer :: IO ()
printAoC2015Day01Answer = do
  putStrLn "------ Day 01 ------"
  putStrLn $ "part1: " ++ show part1
  putStrLn $ "part2: " ++ show part2
  putStrLn ""

calcNextFloor :: Char -> Int
calcNextFloor c
  | c == '(' = 1
  | c == ')' = -1
  | otherwise = 0

floorDirections :: List Int
floorDirections = calcNextFloor <$> input

data Acc = Acc Int Int deriving (Show)

instance Semigroup Acc where
  (<>) :: Acc -> Acc -> Acc
  Acc f i <> Acc f' i' = Acc (f + f') (i + i')

instance Monoid Acc where
  mempty :: Acc
  mempty = Acc 0 0

part1 :: Int
part1 = sum floorDirections

part2 :: Int
part2 = (+ 1) $ length $ scanlWhile (+) (/= (-1)) floorDirections
 where
  scanlWhile f g = takeWhile g . scanl1 f
