printAnswers :: IO ()
printAnswers = do
    putStrLn "Recursive"
    putStrLn $ show part1
    putStrLn $ show part2
    putStrLn ""
    putStrLn "scanl"
    putStrLn $ show part1_1
    putStrLn $ show part2_1
    putStrLn ""
    putStrLn "foldl"
    putStrLn $ show part1_2
    putStrLn $ show part2_2
    putStrLn ""

calcNextFloor :: Char -> Int
calcNextFloor c
    | c == '(' = 1
    | c == ')' = -1
    | otherwise = 0

part1 :: Int
part1 = sum $ calcNextFloor <$> input

findFirstTimeInBasement :: Int -> Int -> String -> Int
findFirstTimeInBasement _ _ [] = -1
findFirstTimeInBasement floorNum numInstructionsProcessed (instruction : restOfInstructions)
    | floorNum == -1 = numInstructionsProcessed
    | otherwise = 
        let newFloor = floorNum + calcNextFloor instruction
            newNumInstructionsProcessed = numInstructionsProcessed + 1
        in
            findFirstTimeInBasement newFloor newNumInstructionsProcessed restOfInstructions

part2 :: Int
part2 = findFirstTimeInBasement 0 0 input

-- Alternate solutions using scanl and takewhile
part1_1 :: Int
part1_1 = last 
    $ scanl (+) 0 
    $ calcNextFloor <$> input

part2_1 :: Int
part2_1 = length
    $ takeWhile (/= (-1))
    $ scanl (+) 0 
    $ calcNextFloor <$> input

-- Alternate solutions using fold
part1_2 :: Int
part1_2 = foldl (+) 0 
    $ calcNextFloor <$> input

foldlWhile :: (b -> a -> b) -> (c -> a -> c) -> (c -> Bool) -> (b, c) -> [a] -> b
foldlWhile _ _ _ (acc, _) [] = acc
foldlWhile foldAccFn predAccFn predFn (foldAccVal, predAccVal) (x:xs)
  | predFn predAccVal = foldAccVal
  | otherwise = foldlWhile foldAccFn predAccFn predFn (foldAccFn foldAccVal x, predAccFn predAccVal x) xs

part2_2 :: Int
part2_2 = 
    let countFn = (\acc _ -> acc + 1)
    in 
    foldlWhile countFn (+) (== (-1)) (0, 0)
        $ calcNextFloor <$> input
