printAoC2015Day01Answer :: IO ()
printAoC2015Day01Answer = do
  putStrLn "------ Day 01 ------"
  putStrLn "Recursive"
  putStrLn $ "part1: " ++ show part1
  putStrLn $ "part2: " ++ show part2
  putStrLn ""
  putStrLn "scanl"
  putStrLn $ "part1: " ++ show part1_1
  putStrLn $ "part2: " ++ show part2_1
  putStrLn ""
  putStrLn "foldl"
  putStrLn $ "part1: " ++ show part1_2
  putStrLn $ "part2: " ++ show part2_2
  putStrLn ""

calcNextFloor :: Char -> Int
calcNextFloor c
  | c == '(' = 1
  | c == ')' = -1
  | otherwise = 0

floorDirections :: [Int]
floorDirections = calcNextFloor <$> input

part1 :: Int
part1 = sum $ floorDirections

findFirstTimeInBasement :: Int -> Int -> String -> Int
findFirstTimeInBasement _ _ [] = -1
findFirstTimeInBasement floorNum numInstructionsProcessed (instruction : restOfInstructions)
  | floorNum == -1 = numInstructionsProcessed
  | otherwise =
      let newFloor = floorNum + calcNextFloor instruction
          newNumInstructionsProcessed = numInstructionsProcessed + 1
       in findFirstTimeInBasement newFloor newNumInstructionsProcessed restOfInstructions

part2 :: Int
part2 = findFirstTimeInBasement 0 0 input

-- Alternate solutions using scanl and takewhile
part1_1 :: Int
part1_1 =
  last $ scanl1 (+) floorDirections

scanlUntil :: [Int] -> [Int]
scanlUntil = takeWhile (/= (-1)) . scanl1 (+)

part2_1 :: Int
part2_1 =
  length $ scanlUntil floorDirections

-- Alternate solutions using fold
part1_2 :: Int
part1_2 =
  foldl (+) 0 $ floorDirections

data Acc = Acc Int Int deriving (Show)

foldlWhile :: (b -> a -> b) -> (b -> Bool) -> b -> List a -> b
foldlWhile _ _ acc [] = acc
foldlWhile accFn predFn acc (x : xs) =
  if predFn acc
    then acc
    else foldlWhile accFn predFn (accFn acc x) xs

part2_2 :: Acc
part2_2 =
  let countFn = (\acc _ -> acc + 1)
      foldFn = (\acc x -> acc + x)
      accFn (Acc b c) a = Acc (countFn b a) (foldFn c a)
      predFn = (\(Acc _ b) -> b == (-1))
   in foldlWhile accFn predFn (Acc 0 0) $
        floorDirections
